using Blazor.DataEntities;
using Microsoft.AspNetCore.Components.Sections;

namespace Blazor.Services.KennelService;

public class KennelService : IKennelService
{
    private List<Dog> Dogs;

    public KennelService()
    {
        Dogs = new List<Dog>();
        GenerateDogs();
    }

    public async Task<Dog> RegisterDogAsync(Dog dog)
    {
        if (!ValidateDog(dog)) throw new ArgumentException("Invalid dog parameters");
        dog.ArrivalDate = DateTime.Now;
        dog.Id = Dogs.Count + 1;
        Dogs.Add(dog);
        return dog;
    }

    public async Task<List<Dog>> GetDogsAsync()
    {
        return Dogs;
    }

    private async void GenerateDogs()
    {
        await RegisterDogAsync(new Dog
        {
            Name = "Storm",
            Breed = "Labrador",
            Description = "Brown lab who loves leverpostej",
            Sex = "Male",
            ImageUrl = "data:image/jpeg;base64,"
        });
        await RegisterDogAsync(new Dog
        {
            Name = "Bella",
            Breed = "German Shepherd",
            Description = "Loyal and intelligent, enjoys outdoor activities.",
            Sex = "Female",
            ImageUrl = "https://example.com/images/dogs/bella.jpg"
        });

        await RegisterDogAsync(new Dog
        {
            Name = "Max",
            Breed = "Beagle",
            Description = "Curious and friendly, loves to sniff around.",
            Sex = "Male",
            ImageUrl = "https://example.com/images/dogs/max.jpg"
        });

        await RegisterDogAsync(new Dog
        {
            Name = "Lucy",
            Breed = "Poodle",
            Description = "Playful and smart, enjoys attention and cuddles.",
            Sex = "Female",
            ImageUrl = "https://example.com/images/dogs/lucy.jpg"
        });

        await RegisterDogAsync(new Dog
        {
            Name = "Charlie",
            Breed = "Bulldog",
            Description = "Calm and gentle, loves to relax and nap.",
            Sex = "Male",
            ImageUrl = "https://example.com/images/dogs/charlie.jpg"
        });

    }

    private bool ValidateDog(Dog dog)
    {
        if (dog == null)
        {
            return false; // Invalid if the dog object itself is null
        }

        if (string.IsNullOrEmpty(dog.Breed) ||
            string.IsNullOrEmpty(dog.Description) ||
            string.IsNullOrEmpty(dog.Name) ||
            string.IsNullOrEmpty(dog.Sex) ||
            string.IsNullOrEmpty(dog.ImageUrl))
        {
            return false; // Invalid if any string property is null or empty
        }
        

        return true; // All checks passed, the dog is valid
    }
    
    
}